import React, { useEffect, useRef, useState } from 'react';
import styles from '../styles/MapPage.module.css';
import proj4 from "proj4";
import maplibregl from 'maplibre-gl';

function geoJsonCoordsToLngLatArray(coords) {
  // EPSG:3857 Ï†ïÌôïÌïú Ï†ïÏùò
  const epsg3857 = "+proj=merc +a=6378137 +b=6378137 +lat_ts=0.0 +lon_0=0.0 +x_0=0 +y_0=0 +k=1.0 +units=m +nadgrids=@null +no_defs";
  const wgs84 = "+proj=longlat +ellps=WGS84 +datum=WGS84 +no_defs";
  return coords[0].map(([x, y]) => {
    const [lng, lat] = proj4(epsg3857, wgs84, [x, y]);
    return [lng, lat]; // [Í≤ΩÎèÑ, ÏúÑÎèÑ]
  });
}

const MapPage = () => {
  const mapRef = useRef(null);
  const [address, setAddress] = useState('');
  const [image, setImage] = useState(null);

  useEffect(() => {
    let map;
    let floodSourceData = null;

    // ÏßÄÎèÑ ÏÉùÏÑ±
    map = new maplibregl.Map({
      container: 'map',
      style: 'https://basemaps.cartocdn.com/gl/positron-gl-style/style.json', // Î¨¥Î£å OSM Ïä§ÌÉÄÏùº
      center: [128.3446, 36.1195],
      zoom: 12
    });
    mapRef.current = map;

    map.on('load', () => {
      // Ïπ®ÏàòÌùîÏ†ÅÎèÑ Î∂àÎü¨Ïò§Í∏∞
      fetch('/flood/floodmap.json')
        .then(res => res.json())
        .then(data => {
          // Ï¢åÌëú Î≥ÄÌôò (EPSG:3857 ‚Üí WGS84)
          data.features.forEach(feature => {
            if (feature.geometry?.type === "Polygon") {
              feature.geometry.coordinates = [
                geoJsonCoordsToLngLatArray(feature.geometry.coordinates)
              ];
            }
          });
          floodSourceData = data;

          map.addSource('flood', {
            type: 'geojson',
            data: floodSourceData
          });

          map.addLayer({
            id: 'flood-layer',
            type: 'fill',
            source: 'flood',
            paint: {
              'fill-color': [
                'match',
                ['get', 'FLDN_GRD'],
                '1', '#ff0000',
                '2', '#ff8c00',
                '3', '#ffff00',
                '4', '#00c8ff',
                '5', '#0000ff',
                '#888888'
              ],
              'fill-opacity': 0.5,
              'fill-outline-color': '#333'
            }
          });
        });

      // Í∏∞Ï°¥ ÎßàÏª§ Î∂àÎü¨Ïò§Í∏∞
      fetch('http://localhost:5000/reports')
        .then(res => res.json())
        .then(data => {
          data.forEach(report => {
            if (report.stage === 3) {
              new maplibregl.Marker()
                .setLngLat([report.lng, report.lat])
                .addTo(map);
            }
          });
        });

      // ÏßÄÎèÑ ÌÅ¥Î¶≠ Ïãú Ïã†Í≥†
      map.on('click', (e) => {
        const lngLat = e.lngLat;
        // Ï£ºÏÜå Î≥ÄÌôò (Nominatim Îì± Ïô∏Î∂Ä API ÌïÑÏöî, Ïó¨Í∏∞ÏÑ† ÏÉùÎûµ)
        setAddress(`Í≤ΩÎèÑ: ${lngLat.lng}, ÏúÑÎèÑ: ${lngLat.lat}`);

        if (!image) {
          alert("ÏÇ¨ÏßÑÏùÑ Î®ºÏ†Ä ÏóÖÎ°úÎìú Ìï¥Ï£ºÏÑ∏Ïöî!");
          return;
        }

        const timestamp = new Date().toISOString();
        const formData = new FormData();
        formData.append('lat', lngLat.lat);
        formData.append('lng', lngLat.lng);
        formData.append('address', `Í≤ΩÎèÑ: ${lngLat.lng}, ÏúÑÎèÑ: ${lngLat.lat}`);
        formData.append('timestamp', timestamp);
        formData.append('image', image);

        fetch('http://localhost:5000/report', {
          method: 'POST',
          body: formData
        })
          .then(res => res.json())
          .then(data => {
            alert(data.message);
            if (data.status === "accept") {
              new maplibregl.Marker()
                .setLngLat([lngLat.lng, lngLat.lat])
                .addTo(map);
            }
            if (data.status === "redirect") {
              window.open("https://www.safekorea.go.kr/", "_blank");
            }
          });
      });
    });

    return () => {
      if (map) map.remove();
    };
    // eslint-disable-next-line
  }, [image]);

  return (
    <div className={styles.container}>
      <h2 className={styles.title}>üìç ÎèÑÎ°úÎö´Ïù¥Îã® - ÌïòÏàòÍµ¨ ÏßÄÎèÑ</h2>
      <label>
        ÏÇ¨ÏßÑ ÏóÖÎ°úÎìú:&nbsp;
        <input
          type="file"
          accept="image/*"
          onChange={e => setImage(e.target.files[0])}
        />
      </label>
      <div className={styles.mapArea}>
        <div id="map" style={{ width: '100%', height: '100%' }}></div>
      </div>
      <p>üó∫Ô∏è ÌòÑÏû¨ ÌÅ¥Î¶≠Ìïú Ï£ºÏÜå: <strong>{address}</strong></p>
    </div>
  );
};

export default MapPage;
