import React, { useEffect, useRef, useState } from 'react';
import styles from '../styles/MapPage.module.css';
import proj4 from "proj4";

const utmk = "+proj=tmerc +lat_0=38 +lon_0=127.5 +k=1 +x_0=1000000 +y_0=2000000 +ellps=GRS80 +units=m +no_defs";
const wgs84 = "+proj=longlat +ellps=WGS84 +datum=WGS84 +no_defs";

function wktToLatLngArray(wkt) {
  const matches = wkt.match(/\(\((.+)\)\)/);
  if (!matches) return [];
  return matches[1].split(',').map(pair => {
    const [x, y] = pair.trim().split(' ').map(Number);
    // 좌표 변환
    const [lng, lat] = proj4(utmk, wgs84, [x, y]);
    return new window.kakao.maps.LatLng(lat, lng);
  });
}

const MapPage = () => {
  const mapRef = useRef(null);
  const [address, setAddress] = useState('');
  const [image, setImage] = useState(null);
  const markersRef = useRef([]);

  useEffect(() => {
    // 카카오맵 스크립트 로드
    function initMap() {
      window.kakao.maps.load(() => {
        const container = document.getElementById('map');
        const options = {
          center: new window.kakao.maps.LatLng(36.1195, 128.3446), // 구미시 근처로 지도 중심 이동
          level: 4
        };
        const map = new window.kakao.maps.Map(container, options);
        mapRef.current = map;

        // 1. 침수지역 폴리곤 표시
        fetch('/flood/floodmap.json')
          .then(res => res.json())
          .then(data => {
            data.features.forEach(feature => {
              const geom = feature.properties?.GEOM;
              if (!geom || geom === '지오메트리') return;
              const coords = wktToLatLngArray(geom);

              // 0,0만 있는 좌표는 건너뜀
              if (coords.every(c => c.getLat() === 0 && c.getLng() === 0)) return;

              console.log(coords);

              const grade = Number(feature.properties.FLDN_GRD);
              const colors = [
                'rgba(0,120,255,0.7)',
                'rgba(0,200,255,0.7)',
                'rgba(255,255,0,0.7)',
                'rgba(255,140,0,0.7)',
                'rgba(255,0,0,0.7)'
              ];
              const color = colors[grade - 1] || 'rgba(0,0,0,1)';
              new window.kakao.maps.Polygon({
                map: map,
                path: coords,
                strokeWeight: 4,
                strokeColor: color,
                strokeOpacity: 1,
                fillColor: color,
                fillOpacity: 0.7
              });
            });
          });

        // 기존 마커 불러오기
        fetch('http://localhost:5000/reports')
          .then(res => res.json())
          .then(data => {
            data.forEach(report => {
              if (report.stage === 3) { // 3단계만 마커 표시
                const markerPosition = new window.kakao.maps.LatLng(report.lat, report.lng);
                const marker = new window.kakao.maps.Marker({
                  position: markerPosition,
                  map: map
                });
                markersRef.current.push(marker);
              }
            });
          });

        // 지도 클릭 시 신고
        window.kakao.maps.event.addListener(map, 'click', function (mouseEvent) {
          const latlng = mouseEvent.latLng;
          // 주소 변환
          const geocoder = new window.kakao.maps.services.Geocoder();
          geocoder.coord2Address(latlng.getLng(), latlng.getLat(), function (result, status) {
            if (status === window.kakao.maps.services.Status.OK) {
              const addr = result[0].address.address_name;
              setAddress(addr);

              // 사진 업로드가 선택되지 않은 경우 안내
              if (!image) {
                alert("사진을 먼저 업로드 해주세요!");
                return;
              }

              // 신고 전송 (FormData 사용)
              const timestamp = new Date().toISOString();
              const formData = new FormData();
              formData.append('lat', latlng.getLat());
              formData.append('lng', latlng.getLng());
              formData.append('address', addr);
              formData.append('timestamp', timestamp);
              formData.append('image', image);

              fetch('http://localhost:5000/report', {
                method: 'POST',
                body: formData
              })
                .then(res => res.json())
                .then(data => {
                  alert(data.message);
                  // 3단계일 때만 마커 추가
                  if (data.status === "accept") {
                    const marker = new window.kakao.maps.Marker({
                      position: latlng,
                      map: map
                    });
                    markersRef.current.push(marker);
                  }
                  // 4단계면 공공기관 사이트로 이동
                  if (data.status === "redirect") {
                    window.open("https://www.safekorea.go.kr/", "_blank");
                  }
                });
            }
          });
        });
      });
    }

    if (!window.kakao) {
      const script = document.createElement('script');
      script.src = `https://dapi.kakao.com/v2/maps/sdk.js?appkey=f3431dbe0250d319e254837045e1ef8a&autoload=false&libraries=services`;
      script.async = true;
      document.head.appendChild(script);
      script.onload = initMap;
    } else {
      initMap();
    }
    // eslint-disable-next-line
  }, [image]);

  return (
    <div className={styles.container}>
      <h2 className={styles.title}>📍 도로뚫이단 - 하수구 지도</h2>
      <label>
        사진 업로드:&nbsp;
        <input
          type="file"
          accept="image/*"
          onChange={e => setImage(e.target.files[0])}
        />
      </label>
      <div className={styles.mapArea}>
        <div id="map" style={{ width: '100%', height: '100%' }}></div>
      </div>
      <p>🗺️ 현재 클릭한 주소: <strong>{address}</strong></p>
    </div>
  );
};

export default MapPage;
